window.onload=()=>{new Swiper(".mySwiper",{spaceBetween:10,width:156,direction:"horizontal",loop:!1,breakpoints:{768:{slidesPerView:5,spaceBetween:40},1024:{slidesPerView:5,spaceBetween:40}}});fecthDataSeguros(),fecthDataBancos()};const fecthDataSeguros=async()=>{await fetch("https://api-rastreator.herokuapp.com/seguros").then(e=>e.json()).then(e=>{setSliderOne(e)}).catch(e=>{console.log("Error: ",e)})},fecthDataBancos=async()=>{await fetch("https://api-rastreator.herokuapp.com/bancos").then(e=>e.json()).then(e=>{setSliderTwo(e)}).catch(e=>{console.log("Error: ",e)})},setSliderOne=e=>{const o=document.querySelector(".l-service__slide1 .swiper .swiper-wrapper");e.forEach(e=>{const t=document.createElement("div");t.classList.add("swiper-slide"),o.appendChild(t);const s=document.createElement("p");s.classList.add("slider-title"),s.textContent=e.title,t.appendChild(s);const n=document.createElement("p");n.classList.add("slider-from"),n.textContent="Desde",t.appendChild(n);const a=document.createElement("p");a.classList.add("slider-price"),a.textContent=e.price_from+" €/mes",t.appendChild(a)})},setSliderTwo=e=>{console.log(e);const o=document.querySelector(".l-service__slide2 .swiper .swiper-wrapper");e.forEach(e=>{const t=document.createElement("div");t.classList.add("swiper-slide"),o.appendChild(t);const s=document.createElement("p");s.classList.add("slider-title"),s.textContent="Hasta "+e.max+" €",t.appendChild(s);const n=document.createElement("p");n.classList.add("slider-from"),n.textContent="Cuota desde",t.appendChild(n);const a=document.createElement("p");a.classList.add("slider-price"),a.textContent=e.fee+" €/mes",t.appendChild(a)})};